<script lang="ts">
	import { gsap } from "gsap";
	import { DrawSVGPlugin } from "gsap/DrawSVGPlugin";
	import { onMount, tick } from "svelte";

	const props = $props<{ visible?: boolean; onResume?: () => void }>();
	const initialVisible = props.visible ?? false;
	const visible = $derived(props.visible ?? false);

	let render = $state(initialVisible);
	let mainEl: HTMLElement | null = $state(null);
	let illustrationPath = $state<SVGPathElement | null>(null);
	let revealTimeline: gsap.core.Timeline | null = null;
	let illustrationTimeline: gsap.core.Tween | null = null;
	let lastVisible = $state(initialVisible);

	gsap.registerPlugin(DrawSVGPlugin);

	const cleanupTimelines = () => {
		revealTimeline?.kill();
		revealTimeline = null;
		illustrationTimeline?.kill();
		illustrationTimeline = null;
	};

	const playIllustration = (direction: "in" | "out") => {
		if (!illustrationPath) return;
		illustrationTimeline?.kill();
		if (direction === "in") {
			illustrationTimeline = gsap.fromTo(
				illustrationPath,
				{ drawSVG: "0%" },
				{ drawSVG: "100%", duration: 3, ease: "power2.inOut" },
			);
			return;
		}

		illustrationTimeline = gsap.to(illustrationPath, {
			drawSVG: "0%",
			duration: 1.2,
			ease: "power1.in",
		});
	};

	const playOpen = () => {
		if (!mainEl) return;
		revealTimeline?.kill();
		revealTimeline = gsap.timeline({ defaults: { duration: 0.8, ease: "power3.out" } });
		revealTimeline.fromTo(mainEl, { opacity: 0 }, { opacity: 1 });
		playIllustration("in");
	};

	const playClose = () => {
		if (!mainEl) {
			render = false;
			return;
		}

		revealTimeline?.kill();
		revealTimeline = gsap.timeline({
			defaults: { duration: 0.5, ease: "power2.in" },
			onComplete: () => {
				if (!visible) {
					render = false;
				}
				revealTimeline = null;
			},
		});

		revealTimeline.to(mainEl, { opacity: 0 });
		playIllustration("out");
	};

	function handleResume() {
		props.onResume?.();
	}

	onMount(() => {
		if (visible) {
			playIllustration("in");
		}

		return () => {
			cleanupTimelines();
		};
	});

	$effect(() => {
		if (visible === lastVisible) return;
		lastVisible = visible;

		if (visible) {
			render = true;
			void tick().then(() => {
				playOpen();
			});
			return;
		}

		if (render) {
			playClose();
		}
	});
</script>

